return msg;

msg.payload = msg.payload || {};
const usedMem = msg.payload.totalmem - msg.payload.freemem;
msg.payload = `Memory Info:\nTotal: ${msg.payload.totalmem || 'N/A'}\nFree: ${msg.payload.freemem || 'N/A'}\nUsed: ${usedMem}\nUsage: ${msg.payload.memusage || 'N/A'}%`;

if (msg.payload.memusage > 80) {
    node.warn('High memory usage detected.');
}

return msg;

msg.payload = msg.payload.loadavg || [];
msg.payload = `Load Average:\n1 min: ${msg.payload[0] || 'N/A'}\n5 min: ${msg.payload[1] || 'N/A'}\n15 min: ${msg.payload[2] || 'N/A'}`;

if (msg.payload[0] > 2.0) {
    node.warn('High CPU load detected.');
}

return msg;

if (msg.payload === false) {
    return [msg, null];
} else {
    return [null, msg];
}

if (msg.statusCode === 200) {
    node.status({fill:"green",shape:"dot",text:"Page OK"});
    return null;
} else {
    node.status({fill:"red",shape:"ring",text:"Page FAIL"});
    return [msg, null];
}

if (msg.payload === false) {
    // Node-RED no responde
    global.set("failedRestarts", (global.get("failedRestarts") || 0) + 1);
    msg.failedRestarts = global.get("failedRestarts");
    return msg;
} else {
    // Node-RED responde correctamente
    global.set("failedRestarts", 0);
    return msg;
}

var failedRestarts = msg.failedRestarts || 0;
if (failedRestarts >= 3) {
    // Reiniciar el sistema completo
    return { payload: "sudo reboot" };
} else {
    // Reiniciar solo Node-RED
    return { payload: "sudo systemctl restart nodered" };
}

