const cola = msg.payload;
let flujo = 0;

if (cola.length > 1) {
    const primerRegistro = cola[0];
    const ultimoRegistro = cola[cola.length - 1];
    const tiempoDiferencia = (ultimoRegistro.tiempo - primerRegistro.tiempo) / 1000; // en segundos

    // Convertir los valores de volumen según la escala proporcionada
    const volumenInicial = ((primerRegistro.volumen - 4000) / (20000 - 4000)) * (40000 - 0);
    const volumenFinal = ((ultimoRegistro.volumen - 4000) / (20000 - 4000)) * (40000 - 0);

    if (tiempoDiferencia > 0) {
        flujo = Math.round(-((volumenFinal - volumenInicial) / tiempoDiferencia) * 3600); // calcular flujo
    }
} else {
    // Manejar el caso cuando no hay suficientes registros para calcular un flujo
    flujo = "Insuficientes datos para cálculo de flujo";
}

// Devolver el valor del flujo
return { payload: flujo };

