function setupModbusClient() {
    let modbusClient = flow.get('modbusClient');

    if (!modbusClient) {
        // Asume que estás usando node-red-contrib-modbus
        const ModbusRTU = global.get('ModbusRTU');
        modbusClient = new ModbusRTU();

        modbusClient.connectTCP("192.168.1.75", { port: 502 });
    }

    modbusClient.on('error', function(err) {
        node.error('Error de Modbus: ' + err.message);
        setTimeout(() => {
            node.warn("Reintentando conexión Modbus...");
            modbusClient.connectTCP("192.168.1.75", { port: 502 });
        }, 10000);
    });

    modbusClient.on('connect', function() {
        node.log('Conexión Modbus establecida');
        flow.set('lastSuccessfulCommunication', Date.now());
        node.send({topic: "read_ADC2_AI1", payload: {value:0,fc:4,unitid:1,address:0,quantity:1}});
    });

    return modbusClient;
}

flow.set('modbusClient', setupModbusClient());
