const lastSuccessfulRead = flow.get('lastSuccessfulRead') || 0;
const now = Date.now();
const threshold = 60000; // 60 segundos, ajusta según sea necesario

if (now - lastSuccessfulRead > threshold) {
    // No ha habido lecturas exitosas en el último minuto
    node.status({fill:"red",shape:"ring",text:"No lecturas recientes"});
    // Enviar mensaje para reiniciar el flujo
    return {payload: "full_reset", topic: "watchdog_full_reset"};
} else {
    node.status({fill:"green",shape:"dot",text:"Lecturas OK"});
    return null; // No hacer nada si las lecturas son recientes
}

if (msg.topic === "watchdog_full_reset") {
    // Evitar bucles infinitos de reinicio
    const lastResetTime = flow.get('lastResetTime') || 0;
    const now = Date.now();
    if (now - lastResetTime < 300000) { // 5 minutos
        node.warn("Intento de reinicio muy frecuente, abortando.");
        return null;
    }

    // Marcar el tiempo de este reinicio
    flow.set('lastResetTime', now);

    // Reinicializar todos los estados relevantes del flujo
    flow.set('lastSuccessfulRead', 0);

    // Detener todos los nodos inyectores
    const stopMsg = {payload: "stop"};

    // Reiniciar todos los nodos inyectores después de un breve retraso
    const startMsg = {payload: "start"};

    return [
        {topic: "control", payload: stopMsg},
        {topic: "control", payload: startMsg, delay: 5000} // 5 segundos de retraso
    ];
} else {
    return null;
}

